mapper.select:
select

  distinct


from synthesis




  order by ${orderByClause}


  limit #{startRow} , #{pageSize}

mapper.insert:
insert into synthesis (collid, name, img, 
  sisbigtime, sisendtime, limitcount, 
  synthesized, userlimit, matsci
  )
values (#{collid,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{img,jdbcType=VARCHAR}, 
  #{sisbigtime,jdbcType=TIMESTAMP}, #{sisendtime,jdbcType=TIMESTAMP}, #{limitcount,jdbcType=INTEGER}, 
  #{synthesized,jdbcType=INTEGER}, #{userlimit,jdbcType=INTEGER}, #{matsci,jdbcType=LONGVARCHAR}
  )
