mapper.select:
select

  distinct


from users




  order by ${orderByClause}


  limit #{startRow} , #{pageSize}

mapper.update:
update users
set money = money + ${addMoney}
where user_id = #{userId,jdbcType=INTEGER} and money = #{money}

UPDATE users
SET money = users.money + #{money,jdbcType=DECIMAL}
WHERE user_id = #{userId,jdbcType=INTEGER}

mapper.select:
select users.*, temp.count_number as intervalInvitationCount
from users
       right join (select invitation_id as id, count(invitation_id) as count_number
                   from users
                   where realnametype = #{realNameType,jdbcType=INTEGER}
                     and users.create_time between #{startDate,jdbcType=DATE}
                     and #{endDate,jdbcType=DATE}
                   group by invitation_id) as temp
                  on users.user_id = temp.id order by temp.count_number desc limit 0,#{count,jdbcType=INTEGER}

mapper.update:
UPDATE users
SET balance = #{balance,jdbcType=DECIMAL}
WHERE user_id = #{userId,jdbcType=INTEGER} and balance = #{oldBalance,jdbcType=DECIMAL}

mapper.select:
select ${columns}
from users
where user_id in

    #{uid}
